import React, { useEffect, useState } from "react";
import axios from "axios";
import { useForm } from "react-hook-form";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [mlRecommendations, setMlRecommendations] = useState(null);
  const [mlLoading, setMlLoading] = useState(false);
  const [showEditForm, setShowEditForm] = useState(false);
  const [formLoading, setFormLoading] = useState(false);
  const [resumeUploading, setResumeUploading] = useState(false);
  const { register, handleSubmit, formState: { errors }, reset, setError, setValue, watch } = useForm();

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(
          process.env.REACT_APP_API_URL + "/auth/profile",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setUser(res.data.data.user);
        if (res.data.data.user.role === "jobseeker") {
          const apps = await axios.get(
            process.env.REACT_APP_API_URL + "/users/jobseeker/dashboard",
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setApplications(apps.data.data.applications);
        }
      } catch (err) {
        setUser(null);
        setApplications([]);
      }
      setLoading(false);
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const fetchRecommendations = async () => {
      if (!user || !user.resume?.path) return;
      setMlLoading(true);
      try {
        // Download the resume file as a blob
        const resumeUrl = process.env.REACT_APP_API_URL.replace(/\/api$/, "") + "/" + user.resume.path;
        const resumeBlob = await axios.get(resumeUrl, { responseType: "blob" });
        const formData = new FormData();
        formData.append("resume", resumeBlob.data, user.resume.filename);
        const mlRes = await axios.post(
          process.env.REACT_APP_ML_API_URL + "/ml/match",
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );
        setMlRecommendations(mlRes.data);
      } catch (err) {
        setMlRecommendations({ error: "Could not get recommendations." });
      }
      setMlLoading(false);
    };
    if (user && user.resume?.path) fetchRecommendations();
    // eslint-disable-next-line
  }, [user]);

  // Populate form when user data is loaded
  useEffect(() => {
    if (user && showEditForm) {
      setValue("firstName", user.firstName || "");
      setValue("lastName", user.lastName || "");
      setValue("phone", user.phone || "");
      setValue("city", user.location?.city || "");
      setValue("state", user.location?.state || "");
      setValue("country", user.location?.country || "");
      setValue("skills", user.skills?.join(", ") || "");
      setValue("experienceYears", user.experience?.years || "");
      setValue("experienceLevel", user.experience?.level || "entry");
      setValue("linkedin", user.socialLinks?.linkedin || "");
      setValue("github", user.socialLinks?.github || "");
      setValue("portfolio", user.socialLinks?.portfolio || "");
      setValue("salaryMin", user.preferences?.salaryRange?.min || "");
      setValue("salaryMax", user.preferences?.salaryRange?.max || "");
      setValue("remotePreference", user.preferences?.remotePreference || "hybrid");
      setValue("jobAlerts", user.preferences?.jobAlerts !== false);
      setValue("emailNotifications", user.preferences?.emailNotifications !== false);
    }
  }, [user, showEditForm, setValue]);

  const onSubmitProfile = async (data) => {
    setFormLoading(true);
    try {
      const token = localStorage.getItem("token");
      
      // Prepare profile data
      const profileData = {
        firstName: data.firstName,
        lastName: data.lastName,
        phone: data.phone,
        location: {
          city: data.city,
          state: data.state,
          country: data.country
        },
        skills: data.skills ? data.skills.split(',').map(skill => skill.trim().toLowerCase()) : [],
        experience: {
          years: parseInt(data.experienceYears) || 0,
          level: data.experienceLevel
        },
        socialLinks: {
          linkedin: data.linkedin,
          github: data.github,
          portfolio: data.portfolio
        },
        preferences: {
          salaryRange: {
            min: parseInt(data.salaryMin) || 0,
            max: parseInt(data.salaryMax) || 0
          },
          remotePreference: data.remotePreference,
          jobAlerts: data.jobAlerts,
          emailNotifications: data.emailNotifications
        }
      };

      // Add education if provided
      if (data.education) {
        profileData.education = data.education.split('\n').map(edu => {
          const [degree, institution, field, year] = edu.split('|').map(item => item.trim());
          return {
            degree,
            institution,
            field,
            graduationYear: parseInt(year)
          };
        }).filter(edu => edu.degree && edu.institution && edu.field && edu.graduationYear);
      }

      await axios.put(
        process.env.REACT_APP_API_URL + "/auth/profile",
        profileData,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Refresh user data
      const res = await axios.get(
        process.env.REACT_APP_API_URL + "/auth/profile",
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(res.data.data.user);
      setShowEditForm(false);
    } catch (err) {
      setError("api", { message: err.response?.data?.message || "Failed to update profile" });
    }
    setFormLoading(false);
  };

  const handleResumeUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setResumeUploading(true);
    try {
      const token = localStorage.getItem("token");
      const formData = new FormData();
      formData.append("resume", file);

      await axios.post(
        process.env.REACT_APP_API_URL + "/auth/upload-resume",
        formData,
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data"
          } 
        }
      );

      // Refresh user data
      const res = await axios.get(
        process.env.REACT_APP_API_URL + "/auth/profile",
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(res.data.data.user);
    } catch (err) {
      setError("resume", { message: err.response?.data?.message || "Failed to upload resume" });
    }
    setResumeUploading(false);
  };

  if (loading) return <div>Loading...</div>;
  if (!user) return <div>Profile not found.</div>;

  return (
    <div className="max-w-4xl mx-auto">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold">Profile</h2>
        {user.role === "jobseeker" && (
          <button
            onClick={() => setShowEditForm(!showEditForm)}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            {showEditForm ? "Cancel" : "Edit Profile"}
          </button>
        )}
      </div>

      {/* Profile Edit Form for Job Seekers */}
      {showEditForm && user.role === "jobseeker" && (
        <div className="bg-white rounded shadow p-6 mb-6">
          <h3 className="text-lg font-semibold mb-4">Edit Profile</h3>
          <form onSubmit={handleSubmit(onSubmitProfile)} className="space-y-4">
            <div className="grid md:grid-cols-2 gap-4">
              {/* Basic Information */}
              <div>
                <label className="block text-sm font-medium mb-1">First Name *</label>
                <input
                  type="text"
                  {...register("firstName", { required: "First name is required" })}
                  className="w-full border rounded px-3 py-2"
                />
                {errors.firstName && <span className="text-red-500 text-sm">{errors.firstName.message}</span>}
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Last Name *</label>
                <input
                  type="text"
                  {...register("lastName", { required: "Last name is required" })}
                  className="w-full border rounded px-3 py-2"
                />
                {errors.lastName && <span className="text-red-500 text-sm">{errors.lastName.message}</span>}
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Phone</label>
                <input
                  type="tel"
                  {...register("phone")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="+1-555-123-4567"
                />
                {errors.phone && <span className="text-red-500 text-sm">{errors.phone.message}</span>}
              </div>
            </div>

            <div className="grid md:grid-cols-3 gap-4">
              {/* Location */}
              <div>
                <label className="block text-sm font-medium mb-1">City</label>
                <input
                  type="text"
                  {...register("city")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="City"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">State/Province</label>
                <input
                  type="text"
                  {...register("state")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="State"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Country</label>
                <input
                  type="text"
                  {...register("country")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="Country"
                />
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              {/* Skills and Experience */}
              <div>
                <label className="block text-sm font-medium mb-1">Skills (comma separated)</label>
                <input
                  type="text"
                  {...register("skills")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="javascript, react, node.js, python"
                />
                {errors.skills && <span className="text-red-500 text-sm">{errors.skills.message}</span>}
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Years of Experience</label>
                <input
                  type="number"
                  {...register("experienceYears")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="3"
                  min="0"
                  max="50"
                />
                {errors.experienceYears && <span className="text-red-500 text-sm">{errors.experienceYears.message}</span>}
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Experience Level</label>
                <select
                  {...register("experienceLevel")}
                  className="w-full border rounded px-3 py-2"
                >
                  <option value="entry">Entry Level</option>
                  <option value="mid">Mid Level</option>
                  <option value="senior">Senior Level</option>
                  <option value="lead">Lead</option>
                  <option value="executive">Executive</option>
                </select>
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Education (one per line: Degree|Institution|Field|Year)</label>
              <textarea
                {...register("education")}
                className="w-full border rounded px-3 py-2"
                rows="4"
                placeholder="Bachelor of Science|University of Technology|Computer Science|2020&#10;Master of Business Administration|Business School|Technology Management|2022"
              />
              <p className="text-xs text-gray-500 mt-1">Format: Degree|Institution|Field|Graduation Year</p>
            </div>

            <div className="grid md:grid-cols-3 gap-4">
              {/* Social Links */}
              <div>
                <label className="block text-sm font-medium mb-1">LinkedIn URL</label>
                <input
                  type="url"
                  {...register("linkedin")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="https://linkedin.com/in/username"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">GitHub URL</label>
                <input
                  type="url"
                  {...register("github")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="https://github.com/username"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Portfolio URL</label>
                <input
                  type="url"
                  {...register("portfolio")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="https://portfolio.com"
                />
              </div>
            </div>

            <div className="grid md:grid-cols-2 gap-4">
              {/* Salary Preferences */}
              <div>
                <label className="block text-sm font-medium mb-1">Minimum Salary</label>
                <input
                  type="number"
                  {...register("salaryMin")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="50000"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Maximum Salary</label>
                <input
                  type="number"
                  {...register("salaryMax")}
                  className="w-full border rounded px-3 py-2"
                  placeholder="80000"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Remote Preference</label>
                <select
                  {...register("remotePreference")}
                  className="w-full border rounded px-3 py-2"
                >
                  <option value="remote">Remote</option>
                  <option value="hybrid">Hybrid</option>
                  <option value="onsite">On-site</option>
                </select>
              </div>
            </div>

            <div className="flex gap-4">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  {...register("jobAlerts")}
                  className="mr-2"
                />
                <span className="text-sm">Receive job alerts</span>
              </label>

              <label className="flex items-center">
                <input
                  type="checkbox"
                  {...register("emailNotifications")}
                  className="mr-2"
                />
                <span className="text-sm">Email notifications</span>
              </label>
            </div>

            {errors.api && <div className="text-red-500 text-sm">{errors.api.message}</div>}

            <div className="flex gap-4">
              <button
                type="submit"
                disabled={formLoading}
                className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 disabled:opacity-50"
              >
                {formLoading ? "Saving..." : "Save Profile"}
              </button>
              <button
                type="button"
                onClick={() => setShowEditForm(false)}
                className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Resume Upload Section */}
      {user.role === "jobseeker" && (
        <div className="bg-white rounded shadow p-6 mb-6">
          <h3 className="text-lg font-semibold mb-4">Resume</h3>
          <div className="space-y-4">
            {user.resume ? (
              <div className="flex items-center justify-between p-4 bg-gray-50 rounded">
                <div>
                  <div className="font-medium">{user.resume.filename}</div>
                  <div className="text-sm text-gray-500">
                    Uploaded: {new Date(user.resume.uploadedAt).toLocaleDateString()}
                  </div>
                </div>
                <a
                  href={process.env.REACT_APP_API_URL.replace(/\/api$/, "") + "/" + user.resume.path}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:text-blue-800 text-sm"
                >
                  View Resume
                </a>
              </div>
            ) : (
              <div className="text-gray-500 text-sm">No resume uploaded yet.</div>
            )}
            
            <div>
              <label className="block text-sm font-medium mb-2">
                {user.resume ? "Update Resume" : "Upload Resume"}
              </label>
              <input
                type="file"
                accept=".pdf,.doc,.docx"
                onChange={handleResumeUpload}
                disabled={resumeUploading}
                className="w-full border rounded px-3 py-2"
              />
              {resumeUploading && <div className="text-sm text-gray-500 mt-1">Uploading...</div>}
              {errors.resume && <div className="text-red-500 text-sm mt-1">{errors.resume.message}</div>}
              <p className="text-xs text-gray-500 mt-1">Accepted formats: PDF, DOC, DOCX</p>
            </div>
          </div>
        </div>
      )}

      {/* Profile Display */}
      <div className="bg-white rounded shadow p-6 mb-6">
        <h3 className="text-lg font-semibold mb-4">Profile Information</h3>
        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <div className="mb-4">
              <div className="font-semibold text-lg">{user.firstName} {user.lastName}</div>
              <div className="text-gray-600">{user.email}</div>
              <div className="text-gray-500 text-sm">Role: {user.role}</div>
              {user.phone && <div className="text-gray-600">{user.phone}</div>}
            </div>

            {user.location && (user.location.city || user.location.state || user.location.country) && (
              <div className="mb-4">
                <div className="font-medium text-sm text-gray-700">Location</div>
                <div className="text-gray-600">
                  {[user.location.city, user.location.state, user.location.country]
                    .filter(Boolean)
                    .join(", ")}
                </div>
              </div>
            )}

            {user.skills && user.skills.length > 0 && (
              <div className="mb-4">
                <div className="font-medium text-sm text-gray-700">Skills</div>
                <div className="text-gray-600">{user.skills.join(", ")}</div>
              </div>
            )}

            {user.experience && (
              <div className="mb-4">
                <div className="font-medium text-sm text-gray-700">Experience</div>
                <div className="text-gray-600">
                  {user.experience.years} years ({user.experience.level} level)
                </div>
              </div>
            )}
          </div>

          <div>
            {user.education && user.education.length > 0 && (
              <div className="mb-4">
                <div className="font-medium text-sm text-gray-700">Education</div>
                <div className="space-y-2">
                  {user.education.map((edu, index) => (
                    <div key={index} className="text-gray-600">
                      <div className="font-medium">{edu.degree}</div>
                      <div className="text-sm">{edu.institution}</div>
                      <div className="text-sm">{edu.field}  {edu.graduationYear}</div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {user.socialLinks && (
              <div className="mb-4">
                <div className="font-medium text-sm text-gray-700">Social Links</div>
                <div className="space-y-1">
                  {user.socialLinks.linkedin && (
                    <a href={user.socialLinks.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800 text-sm block">
                      LinkedIn
                    </a>
                  )}
                  {user.socialLinks.github && (
                    <a href={user.socialLinks.github} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800 text-sm block">
                      GitHub
                    </a>
                  )}
                  {user.socialLinks.portfolio && (
                    <a href={user.socialLinks.portfolio} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800 text-sm block">
                      Portfolio
                    </a>
                  )}
                </div>
              </div>
            )}

            {user.preferences && (
              <div className="mb-4">
                <div className="font-medium text-sm text-gray-700">Preferences</div>
                <div className="text-gray-600 text-sm">
                  <div>Remote: {user.preferences.remotePreference}</div>
                  {user.preferences.salaryRange && (
                    <div>
                      Salary: ${user.preferences.salaryRange.min} - ${user.preferences.salaryRange.max}
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* AI Recommendations for Job Seekers */}
      {user.role === "jobseeker" && (
        <div className="bg-white rounded shadow p-6 mb-6">
          <h3 className="text-lg font-semibold mb-4">AI-Powered Job Recommendations</h3>
          {mlLoading ? (
            <div>Loading recommendations...</div>
          ) : mlRecommendations && mlRecommendations.success ? (
            <div>
              <div className="mb-4">
                <div className="text-sm text-gray-600 mb-2">
                  <strong>Extracted Skills:</strong> {mlRecommendations.skills?.join(", ")}
                </div>
                <div className="text-sm text-gray-600">
                  <strong>Experience Years:</strong> {mlRecommendations.experience_years}
                </div>
              </div>
              <div className="space-y-3">
                {mlRecommendations.recommendations?.map((rec, idx) => (
                  <div key={idx} className="bg-blue-50 rounded p-4 border border-blue-200">
                    <div className="font-semibold text-blue-900">{rec.job.title}</div>
                    <div className="text-sm text-blue-700 mb-2">
                      {rec.job.company?.name}  {rec.job.location?.city}
                    </div>
                    <div className="text-xs text-blue-600">
                      Match: {(rec.similarity * 100).toFixed(1)}%  {rec.job.salary?.currency} {rec.job.salary?.min} - {rec.job.salary?.max}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : mlRecommendations && mlRecommendations.error ? (
            <div className="text-red-500 text-sm">{mlRecommendations.error}</div>
          ) : (
            <div className="text-gray-500 text-sm">
              {user.resume ? "Upload a resume to get AI-powered job recommendations." : "Upload a resume to get AI-powered job recommendations."}
            </div>
          )}
        </div>
      )}

      {/* Applied Jobs for Job Seekers */}
      {user.role === "jobseeker" && (
        <div className="bg-white rounded shadow p-6">
          <h3 className="text-lg font-semibold mb-4">Applied Jobs</h3>
          {applications.length === 0 ? (
            <div className="text-gray-500">No applications yet.</div>
          ) : (
            <div className="space-y-3">
              {applications.map((app) => (
                <div key={app._id} className="bg-gray-50 rounded p-4 border border-gray-200">
                  <div className="font-semibold">{app.job?.title}</div>
                  <div className="text-sm text-gray-600 mb-1">
                    {app.job?.company?.name}  {app.job?.location?.city}
                  </div>
                  <div className="text-xs text-gray-500">
                    Applied: {new Date(app.createdAt).toLocaleDateString()}  Status: {app.status}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Profile;
