import React, { useEffect, useState } from "react";
import axios from "axios";

const RecruiterDashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboard = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(
          process.env.REACT_APP_API_URL + "/users/recruiter/dashboard",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setJobs(res.data.data.jobs);
        setApplications(res.data.data.applications);
      } catch (err) {
        setJobs([]);
        setApplications([]);
      }
      setLoading(false);
    };
    fetchDashboard();
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Recruiter Dashboard</h2>
      {loading ? (
        <div>Loading...</div>
      ) : (
        <div className="grid md:grid-cols-2 gap-8">
          <div>
            <h3 className="text-lg font-semibold mb-2">Your Posted Jobs</h3>
            {jobs.length === 0 ? (
              <div>No jobs posted yet.</div>
            ) : (
              <ul className="space-y-2">
                {jobs.map((job) => (
                  <li key={job._id} className="bg-white rounded shadow p-3">
                    <div className="font-semibold">{job.title}</div>
                    <div className="text-sm text-gray-500">{job.location?.city}, {job.location?.country}</div>
                  </li>
                ))}
              </ul>
            )}
          </div>
          <div>
            <h3 className="text-lg font-semibold mb-2">Applicants</h3>
            {applications.length === 0 ? (
              <div>No applications yet.</div>
            ) : (
              <ul className="space-y-2">
                {applications.map((app) => (
                  <li key={app._id} className="bg-white rounded shadow p-3">
                    <div className="font-semibold">{app.applicant?.firstName} {app.applicant?.lastName}</div>
                    <div className="text-sm text-gray-500">Applied for: {app.job?.title}</div>
                    <div className="text-xs text-gray-400">Status: {app.status}</div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default RecruiterDashboard;
