import React, { useEffect, useState } from "react";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [mlRecommendations, setMlRecommendations] = useState(null);
  const [mlLoading, setMlLoading] = useState(false);

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(
          process.env.REACT_APP_API_URL + "/auth/profile",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setUser(res.data.data.user);
        if (res.data.data.user.role === "jobseeker") {
          const apps = await axios.get(
            process.env.REACT_APP_API_URL + "/users/jobseeker/dashboard",
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setApplications(apps.data.data.applications);
        }
      } catch (err) {
        setUser(null);
        setApplications([]);
      }
      setLoading(false);
    };
    fetchProfile();
  }, []);

  useEffect(() => {
    const fetchRecommendations = async () => {
      if (!user || !user.resume?.path) return;
      setMlLoading(true);
      try {
        // Download the resume file as a blob
        const resumeUrl = process.env.REACT_APP_API_URL.replace(/\/api$/, "") + "/" + user.resume.path;
        const resumeBlob = await axios.get(resumeUrl, { responseType: "blob" });
        const formData = new FormData();
        formData.append("resume", resumeBlob.data, user.resume.filename);
        const mlRes = await axios.post(
          process.env.REACT_APP_ML_API_URL + "/ml/match",
          formData,
          { headers: { "Content-Type": "multipart/form-data" } }
        );
        setMlRecommendations(mlRes.data);
      } catch (err) {
        setMlRecommendations({ error: "Could not get recommendations." });
      }
      setMlLoading(false);
    };
    if (user && user.resume?.path) fetchRecommendations();
    // eslint-disable-next-line
  }, [user]);

  if (loading) return <div>Loading...</div>;
  if (!user) return <div>Profile not found.</div>;

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Profile</h2>
      <div className="mb-4">
        <div className="font-semibold">{user.firstName} {user.lastName}</div>
        <div className="text-gray-600">{user.email}</div>
        <div className="text-gray-500 text-sm">Role: {user.role}</div>
        {user.skills && user.skills.length > 0 && (
          <div className="mt-2 text-sm">Skills: {user.skills.join(", ")}</div>
        )}
      </div>
      {user.role === "jobseeker" && (
        <>
          <div className="mb-6">
            <h3 className="text-lg font-semibold mb-2">AI-Powered Job Recommendations</h3>
            {mlLoading ? (
              <div>Loading recommendations...</div>
            ) : mlRecommendations && mlRecommendations.success ? (
              <div>
                <div className="mb-2 text-sm">Extracted Skills: {mlRecommendations.skills?.join(", ")}</div>
                <div className="mb-2 text-sm">Experience Years: {mlRecommendations.experience_years}</div>
                <ul className="space-y-2">
                  {mlRecommendations.recommendations?.map((rec, idx) => (
                    <li key={idx} className="bg-blue-50 rounded p-3">
                      <div className="font-semibold">{rec.job.title}</div>
                      <div className="text-xs text-gray-500">{rec.job.company?.name} &middot; {rec.job.location?.city}</div>
                      <div className="text-xs text-gray-400">Similarity: {(rec.similarity * 100).toFixed(1)}%</div>
                    </li>
                  ))}
                </ul>
              </div>
            ) : mlRecommendations && mlRecommendations.error ? (
              <div className="text-red-500 text-sm">{mlRecommendations.error}</div>
            ) : (
              <div className="text-gray-500 text-sm">Upload a resume to get recommendations.</div>
            )}
          </div>
          <div>
            <h3 className="text-lg font-semibold mb-2">Applied Jobs</h3>
            {applications.length === 0 ? (
              <div>No applications yet.</div>
            ) : (
              <ul className="space-y-2">
                {applications.map((app) => (
                  <li key={app._id} className="bg-gray-100 rounded p-3">
                    <div className="font-semibold">{app.job?.title}</div>
                    <div className="text-xs text-gray-500">Status: {app.status}</div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Profile;
