import React from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();
  // TODO: Replace with real auth state
  const isAuthenticated = !!localStorage.getItem("token");
  const role = localStorage.getItem("role");

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <nav className="bg-white shadow mb-4">
      <div className="container mx-auto flex justify-between items-center py-3 px-4">
        <Link to="/jobs" className="text-xl font-bold text-blue-600">AI Job Portal</Link>
        <div className="flex gap-4 items-center">
          <Link to="/jobs" className="hover:text-blue-600">Jobs</Link>
          {isAuthenticated && <Link to="/profile" className="hover:text-blue-600">Profile</Link>}
          {isAuthenticated && role === "recruiter" && (
            <Link to="/recruiter/dashboard" className="hover:text-blue-600">Recruiter Dashboard</Link>
          )}
          {!isAuthenticated && <Link to="/login" className="hover:text-blue-600">Login</Link>}
          {!isAuthenticated && <Link to="/register" className="hover:text-blue-600">Register</Link>}
          {isAuthenticated && (
            <button onClick={handleLogout} className="ml-2 px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600">Logout</button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
