import React from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const { register, handleSubmit, formState: { errors }, setError } = useForm();
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      const res = await axios.post(
        process.env.REACT_APP_API_URL + "/auth/register",
        data
      );
      localStorage.setItem("token", res.data.data.token);
      localStorage.setItem("role", res.data.data.user.role);
      navigate("/jobs");
    } catch (err) {
      setError("api", { message: err.response?.data?.message || "Registration failed" });
    }
  };

  return (
    <div className="max-w-md mx-auto bg-white p-8 rounded shadow">
      <h2 className="text-2xl font-bold mb-6 text-center">Register</h2>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        <div>
          <label className="block mb-1">First Name</label>
          <input type="text" {...register("firstName", { required: true })} className="w-full border rounded px-3 py-2" />
          {errors.firstName && <span className="text-red-500 text-sm">First name is required</span>}
        </div>
        <div>
          <label className="block mb-1">Last Name</label>
          <input type="text" {...register("lastName", { required: true })} className="w-full border rounded px-3 py-2" />
          {errors.lastName && <span className="text-red-500 text-sm">Last name is required</span>}
        </div>
        <div>
          <label className="block mb-1">Email</label>
          <input type="email" {...register("email", { required: true })} className="w-full border rounded px-3 py-2" />
          {errors.email && <span className="text-red-500 text-sm">Email is required</span>}
        </div>
        <div>
          <label className="block mb-1">Password</label>
          <input type="password" {...register("password", { required: true, minLength: 6 })} className="w-full border rounded px-3 py-2" />
          {errors.password && <span className="text-red-500 text-sm">Password is required (min 6 chars)</span>}
        </div>
        <div>
          <label className="block mb-1">Role</label>
          <select {...register("role", { required: true })} className="w-full border rounded px-3 py-2">
            <option value="jobseeker">Job Seeker</option>
            <option value="recruiter">Recruiter</option>
          </select>
        </div>
        {errors.api && <div className="text-red-500 text-sm">{errors.api.message}</div>}
        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">Register</button>
      </form>
    </div>
  );
};

export default Register;
