import React, { useEffect, useState } from "react";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(process.env.REACT_APP_API_URL + "/auth/profile", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(res.data.data);
    } catch (err) {
      console.error("Error fetching profile:", err);
    }
    setLoading(false);
  };

  if (loading) {
    return <div className="text-center py-8">Loading...</div>;
  }

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Profile</h2>
      
      {/* User Information */}
      <div className="bg-white rounded shadow p-6 mb-6">
        <h3 className="text-lg font-semibold mb-4">Personal Information</h3>
        {user && (
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium text-gray-700 mb-3">Basic Information</h4>
              <div className="space-y-2">
                <p><strong>Name:</strong> {user.firstName} {user.lastName}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Role:</strong> {user.role}</p>
                <p><strong>Phone:</strong> {user.phone || 'Not provided'}</p>
              </div>
            </div>
            
            <div>
              <h4 className="font-medium text-gray-700 mb-3">Location</h4>
              <div className="space-y-2">
                <p><strong>City:</strong> {user.location?.city || 'Not provided'}</p>
                <p><strong>State:</strong> {user.location?.state || 'Not provided'}</p>
                <p><strong>Country:</strong> {user.location?.country || 'Not provided'}</p>
              </div>
            </div>

            {user.role === 'jobseeker' && (
              <>
                <div>
                  <h4 className="font-medium text-gray-700 mb-3">Skills & Experience</h4>
                  <div className="space-y-2">
                    <p><strong>Skills:</strong> {user.skills?.join(', ') || 'Not provided'}</p>
                    <p><strong>Experience Level:</strong> {user.experience?.level || 'Not specified'}</p>
                    <p><strong>Years of Experience:</strong> {user.experience?.years || 'Not specified'}</p>
                  </div>
                </div>

                <div>
                  <h4 className="font-medium text-gray-700 mb-3">Education</h4>
                  <div className="space-y-2">
                    {user.education && user.education.length > 0 ? (
                      user.education.map((edu, index) => (
                        <div key={index} className="border-l-2 border-blue-200 pl-3">
                          <p><strong>{edu.degree}</strong></p>
                          <p className="text-sm">{edu.institution}</p>
                          <p className="text-sm">{edu.field} â€¢ {edu.graduationYear}</p>
                        </div>
                      ))
                    ) : (
                      <p>No education information provided</p>
                    )}
                  </div>
                </div>

                <div>
                  <h4 className="font-medium text-gray-700 mb-3">Social Links</h4>
                  <div className="space-y-2">
                    {user.socialLinks?.linkedin && (
                      <p><strong>LinkedIn:</strong> <a href={user.socialLinks.linkedin} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800">View Profile</a></p>
                    )}
                    {user.socialLinks?.github && (
                      <p><strong>GitHub:</strong> <a href={user.socialLinks.github} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800">View Profile</a></p>
                    )}
                    {user.socialLinks?.portfolio && (
                      <p><strong>Portfolio:</strong> <a href={user.socialLinks.portfolio} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800">View Portfolio</a></p>
                    )}
                    {!user.socialLinks?.linkedin && !user.socialLinks?.github && !user.socialLinks?.portfolio && (
                      <p>No social links provided</p>
                    )}
                  </div>
                </div>
              </>
            )}

            {user.role === 'recruiter' && (
              <div>
                <h4 className="font-medium text-gray-700 mb-3">Company Information</h4>
                <div className="space-y-2">
                  <p><strong>Company Name:</strong> {user.company?.name || 'Not provided'}</p>
                  <p><strong>Industry:</strong> {user.company?.industry || 'Not provided'}</p>
                  <p><strong>Company Size:</strong> {user.company?.size || 'Not provided'}</p>
                  {user.company?.website && (
                    <p><strong>Website:</strong> <a href={user.company.website} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:text-blue-800">Visit Website</a></p>
                  )}
                </div>
              </div>
            )}
          </div>
        )}
      </div>

      {/* Account Information */}
      <div className="bg-white rounded shadow p-6">
        <h3 className="text-lg font-semibold mb-4">Account Information</h3>
        <div className="grid md:grid-cols-2 gap-6">
          <div>
            <p><strong>Account Created:</strong> {user && new Date(user.createdAt).toLocaleDateString()}</p>
            <p><strong>Last Updated:</strong> {user && new Date(user.updatedAt).toLocaleDateString()}</p>
            <p><strong>Account Status:</strong> <span className="text-green-600 font-medium">Active</span></p>
          </div>
          <div>
            <button className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
              Edit Profile
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
