import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const JobListings = () => {
  const [jobs, setJobs] = useState([]);
  const [filters, setFilters] = useState({ 
    location: "", 
    skill: "", 
    jobTitle: "", 
    companyName: "",
    workType: "",
    level: ""
  });
  const [loading, setLoading] = useState(false);

  const fetchJobs = async () => {
    setLoading(true);
    try {
      const params = {};
      if (filters.location) params.location = filters.location;
      if (filters.skill) params.skill = filters.skill;
      if (filters.jobTitle) params.jobTitle = filters.jobTitle;
      if (filters.companyName) params.companyName = filters.companyName;
      if (filters.workType) params.workType = filters.workType;
      if (filters.level) params.level = filters.level;
      
      const res = await axios.get(process.env.REACT_APP_API_URL + "/jobs", { params });
      setJobs(res.data.data);
    } catch (err) {
      console.error('Error fetching jobs:', err);
      setJobs([]);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchJobs();
    // eslint-disable-next-line
  }, []);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchJobs();
  };

  const clearFilters = () => {
    setFilters({ 
      location: "", 
      skill: "", 
      jobTitle: "", 
      companyName: "",
      workType: "",
      level: ""
    });
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Job Listings</h2>
      
      {/* Search Filters */}
      <div className="bg-white rounded shadow p-6 mb-6">
        <form onSubmit={handleFilterSubmit} className="space-y-4">
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium mb-1">Job Title</label>
              <input
                type="text"
                name="jobTitle"
                value={filters.jobTitle}
                onChange={handleFilterChange}
                className="w-full border rounded px-3 py-2"
                placeholder="e.g., Software Engineer"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium mb-1">Company Name</label>
              <input
                type="text"
                name="companyName"
                value={filters.companyName}
                onChange={handleFilterChange}
                className="w-full border rounded px-3 py-2"
                placeholder="e.g., Google"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium mb-1">Location</label>
              <input
                type="text"
                name="location"
                value={filters.location}
                onChange={handleFilterChange}
                className="w-full border rounded px-3 py-2"
                placeholder="e.g., San Francisco"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium mb-1">Skill</label>
              <input
                type="text"
                name="skill"
                value={filters.skill}
                onChange={handleFilterChange}
                className="w-full border rounded px-3 py-2"
                placeholder="e.g., JavaScript"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium mb-1">Work Type</label>
              <select
                name="workType"
                value={filters.workType}
                onChange={handleFilterChange}
                className="w-full border rounded px-3 py-2"
              >
                <option value="">All Types</option>
                <option value="remote">Remote</option>
                <option value="hybrid">Hybrid</option>
                <option value="onsite">On-site</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium mb-1">Job Level</label>
              <select
                name="level"
                value={filters.level}
                onChange={handleFilterChange}
                className="w-full border rounded px-3 py-2"
              >
                <option value="">All Levels</option>
                <option value="entry">Entry Level</option>
                <option value="mid">Mid Level</option>
                <option value="senior">Senior Level</option>
                <option value="lead">Lead</option>
                <option value="executive">Executive</option>
              </select>
            </div>
          </div>
          
          <div className="flex gap-4">
            <button
              type="submit"
              className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
            >
              Search Jobs
            </button>
            <button
              type="button"
              onClick={clearFilters}
              className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
            >
              Clear Filters
            </button>
          </div>
        </form>
      </div>

      {/* Job Listings */}
      {loading ? (
        <div className="text-center py-8">Loading jobs...</div>
      ) : jobs.length === 0 ? (
        <div className="text-center py-8 text-gray-500">No jobs found matching your criteria.</div>
      ) : (
        <div className="space-y-4">
          {jobs.map((job) => (
            <div key={job._id} className="bg-white rounded shadow p-6">
              <div className="flex justify-between items-start mb-4">
                <div className="flex-1">
                  <h3 className="text-xl font-semibold text-blue-600 mb-2">
                    <Link to={`/apply/${job._id}`} className="hover:underline">
                      {job.title}
                    </Link>
                  </h3>
                  <p className="text-lg font-medium text-gray-700 mb-1">{job.company?.name}</p>
                  <p className="text-gray-600 mb-2">
                    {job.location?.city}, {job.location?.country} • {job.workType} • {job.jobType}
                  </p>
                  <p className="text-gray-600 mb-3">{job.summary}</p>
                  
                  <div className="flex flex-wrap gap-2 mb-3">
                    {job.requirements?.skills?.slice(0, 5).map((skill, index) => (
                      <span
                        key={index}
                        className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded"
                      >
                        {skill}
                      </span>
                    ))}
                    {job.requirements?.skills?.length > 5 && (
                      <span className="text-gray-500 text-xs">+{job.requirements.skills.length - 5} more</span>
                    )}
                  </div>
                  
                  <div className="flex items-center text-sm text-gray-500">
                    <span>Posted {new Date(job.createdAt).toLocaleDateString()}</span>
                    {job.applicationDeadline && (
                      <span className="ml-4">
                        Deadline: {new Date(job.applicationDeadline).toLocaleDateString()}
                      </span>
                    )}
                  </div>
                </div>
                
                <div className="text-right">
                  <Link
                    to={`/apply/${job._id}`}
                    className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                  >
                    Apply Now
                  </Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default JobListings;
