import React, { useState } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const Apply = () => {
  const { jobId } = useParams();
  const { register, handleSubmit, formState: { errors }, setError } = useForm();
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("coverLetter", data.coverLetter);
      formData.append("resume", data.resume[0]);
      const token = localStorage.getItem("token");
      await axios.post(
        process.env.REACT_APP_API_URL + `/applications/${jobId}`,
        formData,
        { headers: { "Content-Type": "multipart/form-data", Authorization: `Bearer ${token}` } }
      );
      setSuccess(true);
      setTimeout(() => navigate("/jobs"), 2000);
    } catch (err) {
      setError("api", { message: err.response?.data?.message || "Application failed" });
    }
    setLoading(false);
  };

  return (
    <div className="max-w-lg mx-auto bg-white p-8 rounded shadow">
      <h2 className="text-2xl font-bold mb-6 text-center">Apply for Job</h2>
      {success ? (
        <div className="text-green-600 text-center font-semibold">Application submitted! Redirecting...</div>
      ) : (
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          <div>
            <label className="block mb-1">Cover Letter</label>
            <textarea {...register("coverLetter", { required: true })} className="w-full border rounded px-3 py-2" rows={5} />
            {errors.coverLetter && <span className="text-red-500 text-sm">Cover letter is required</span>}
          </div>
          <div>
            <label className="block mb-1">Resume (PDF, DOCX, DOC)</label>
            <input type="file" accept=".pdf,.doc,.docx" {...register("resume", { required: true })} className="w-full" />
            {errors.resume && <span className="text-red-500 text-sm">Resume is required</span>}
          </div>
          {errors.api && <div className="text-red-500 text-sm">{errors.api.message}</div>}
          <button type="submit" disabled={loading} className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700 disabled:opacity-50">{loading ? "Submitting..." : "Apply"}</button>
        </form>
      )}
    </div>
  );
};

export default Apply;
