import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [resume, setResume] = useState(null);
  const [recommendations, setRecommendations] = useState([]);
  const [allJobs, setAllJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchUserProfile();
    fetchAllJobs();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get(process.env.REACT_APP_API_URL + "/auth/profile", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUser(res.data.data);
      if (res.data.data.resume) {
        setResume(res.data.data.resume);
        fetchRecommendations(res.data.data.resume.path);
      }
    } catch (err) {
      console.error("Error fetching profile:", err);
    }
  };

  const fetchAllJobs = async () => {
    try {
      const res = await axios.get(process.env.REACT_APP_API_URL + "/jobs");
      setAllJobs(res.data.data);
    } catch (err) {
      console.error("Error fetching jobs:", err);
    }
    setLoading(false);
  };

  const fetchRecommendations = async (resumePath) => {
    try {
      const formData = new FormData();
      // We'll need to get the actual file from the server
      const token = localStorage.getItem("token");
      const res = await axios.post(
        process.env.REACT_APP_ML_API_URL + "/ml/match",
        formData,
        {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );
      setRecommendations(res.data.recommendations || []);
    } catch (err) {
      console.error("Error fetching recommendations:", err);
    }
  };

  const handleResumeUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    try {
      const token = localStorage.getItem("token");
      const formData = new FormData();
      formData.append("resume", file);

      const res = await axios.post(
        process.env.REACT_APP_API_URL + "/auth/upload-resume",
        formData,
        {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        }
      );

      setResume(res.data.data.resume);
      fetchRecommendations(res.data.data.resume.path);
    } catch (err) {
      console.error("Error uploading resume:", err);
    }
    setUploading(false);
  };

  const getResumeUrl = (resumePath) => {
    if (!resumePath) return null;
    const filename = resumePath.split('/').pop();
    return `${process.env.REACT_APP_API_URL.replace('/api', '')}/files/resumes/${filename}`;
  };

  if (loading) {
    return <div className="text-center py-8">Loading...</div>;
  }

  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Profile</h2>
      
      {/* User Information */}
      <div className="bg-white rounded shadow p-6 mb-6">
        <h3 className="text-lg font-semibold mb-4">Personal Information</h3>
        {user && (
          <div className="grid md:grid-cols-2 gap-4">
            <div>
              <p><strong>Name:</strong> {user.firstName} {user.lastName}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Role:</strong> {user.role}</p>
            </div>
            <div>
              <p><strong>Phone:</strong> {user.phone || 'Not provided'}</p>
              <p><strong>Location:</strong> {user.location?.city}, {user.location?.country}</p>
            </div>
          </div>
        )}
      </div>

      {/* Resume Upload */}
      <div className="bg-white rounded shadow p-6 mb-6">
        <h3 className="text-lg font-semibold mb-4">Resume</h3>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">Upload Resume (PDF)</label>
            <input
              type="file"
              accept=".pdf"
              onChange={handleResumeUpload}
              disabled={uploading}
              className="w-full border rounded px-3 py-2"
            />
            {uploading && <p className="text-blue-600 text-sm mt-2">Uploading...</p>}
          </div>
          
          {resume && (
            <div>
              <p className="text-sm text-gray-600 mb-2">Current Resume:</p>
              <a
                href={getResumeUrl(resume.path)}
                target="_blank"
                rel="noopener noreferrer"
                className="text-blue-600 hover:text-blue-800 underline"
              >
                {resume.filename}
              </a>
            </div>
          )}
        </div>
      </div>

      {/* Job Recommendations */}
      {recommendations.length > 0 && (
        <div className="bg-white rounded shadow p-6 mb-6">
          <h3 className="text-lg font-semibold mb-4">Recommended Jobs Based on Your Resume</h3>
          <div className="space-y-4">
            {recommendations.map((rec, index) => (
              <div key={index} className="border rounded p-4">
                <h4 className="font-semibold text-blue-600">{rec.job.title}</h4>
                <p className="text-gray-700">{rec.job.company?.name}</p>
                <p className="text-sm text-gray-500">
                  Match: {Math.round(rec.similarity * 100)}% • {rec.job.location?.city}, {rec.job.location?.country}
                </p>
                <Link
                  to={`/apply/${rec.job._id}`}
                  className="inline-block mt-2 bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700"
                >
                  Apply Now
                </Link>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* All Available Jobs */}
      <div className="bg-white rounded shadow p-6">
        <h3 className="text-lg font-semibold mb-4">All Available Jobs</h3>
        {allJobs.length === 0 ? (
          <p className="text-gray-500">No jobs available at the moment.</p>
        ) : (
          <div className="space-y-4">
            {allJobs.map((job) => (
              <div key={job._id} className="border rounded p-4">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <h4 className="font-semibold text-blue-600">
                      <Link to={`/apply/${job._id}`} className="hover:underline">
                        {job.title}
                      </Link>
                    </h4>
                    <p className="text-gray-700">{job.company?.name}</p>
                    <p className="text-sm text-gray-500">
                      {job.location?.city}, {job.location?.country} • {job.workType} • {job.jobType}
                    </p>
                    <p className="text-sm text-gray-600 mt-2">
                      {job.summary || job.description.substring(0, 150)}...
                    </p>
                    <div className="flex flex-wrap gap-1 mt-2">
                      {job.requirements?.skills?.slice(0, 3).map((skill, index) => (
                        <span
                          key={index}
                          className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded"
                        >
                          {skill}
                        </span>
                      ))}
                      {job.requirements?.skills?.length > 3 && (
                        <span className="text-gray-500 text-xs">+{job.requirements.skills.length - 3} more</span>
                      )}
                    </div>
                  </div>
                  <Link
                    to={`/apply/${job._id}`}
                    className="bg-green-600 text-white px-3 py-1 rounded text-sm hover:bg-green-700"
                  >
                    Apply
                  </Link>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
