import React, { createContext, useContext, useState, useEffect } from 'react';
import api from '../api';

const AppContext = createContext();

export const useApp = () => {
  const context = useContext(AppContext);
  if (!context) {
    throw new Error('useApp must be used within AppProvider');
  }
  return context;
};

export const AppProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);
  const [jobs, setJobs] = useState([]); // Public jobs (for job seekers)
  const [recruiterJobs, setRecruiterJobs] = useState([]); // Recruiter's posted jobs
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  // Initialize user from localStorage
  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');
    if (token && role) {
      fetchUserProfile();
    } else {
      setLoading(false);
    }
  }, []);

  // Fetch user profile
  const fetchUserProfile = async () => {
    try {
      const res = await api.get('/auth/profile');
      setCurrentUser(res.data.data.user || res.data.data);
    } catch (err) {
      console.error('Error fetching profile:', err);
      // Clear invalid token
      localStorage.removeItem('token');
      localStorage.removeItem('role');
      setCurrentUser(null);
    } finally {
      setLoading(false);
    }
  };

  // Fetch all jobs
  const fetchJobs = async (filters = {}) => {
    try {
      const params = new URLSearchParams();
      Object.keys(filters).forEach(key => {
        if (filters[key]) params.append(key, filters[key]);
      });
      
      const res = await api.get(`/jobs?${params.toString()}`);
      setJobs(res.data.data || []);
      return res.data.data || [];
    } catch (err) {
      console.error('Error fetching jobs:', err);
      setJobs([]);
      return [];
    }
  };

  // Fetch recruiter's posted jobs
  const fetchRecruiterJobs = async () => {
    try {
      const res = await api.get('/jobs/my-jobs');
      setJobs(res.data.data || []);
      return res.data.data || [];
    } catch (err) {
      console.error('Error fetching recruiter jobs:', err);
      setJobs([]);
      return [];
    }
  };

  // Fetch applications
  const fetchApplications = async () => {
    try {
      const role = localStorage.getItem('role');
      if (role === 'jobseeker') {
        const res = await api.get('/applications/my-applications');
        setApplications(res.data.data || []);
        return res.data.data || [];
      } else if (role === 'recruiter') {
        const res = await api.get('/applications/my-applications');
        setApplications(res.data.data || []);
        return res.data.data || [];
      }
    } catch (err) {
      console.error('Error fetching applications:', err);
      setApplications([]);
      return [];
    }
  };

  // Login
  const login = (token, user) => {
    localStorage.setItem('token', token);
    localStorage.setItem('role', user.role);
    setCurrentUser(user);
  };

  // Logout
  const logout = () => {
    localStorage.clear();
    setCurrentUser(null);
    setJobs([]);
    setApplications([]);
  };

  // Add job
  const addJob = (job) => {
    setJobs(prev => [job, ...prev]);
  };

  // Add application
  const addApplication = (application) => {
    setApplications(prev => [application, ...prev]);
  };

  // Update application
  const updateApplication = (applicationId, updates) => {
    setApplications(prev =>
      prev.map(app =>
        app._id === applicationId ? { ...app, ...updates } : app
      )
    );
  };

  const value = {
    currentUser,
    jobs,
    applications,
    loading,
    login,
    logout,
    fetchUserProfile,
    fetchJobs,
    fetchRecruiterJobs,
    fetchApplications,
    addJob,
    addApplication,
    updateApplication,
    setJobs,
    setApplications,
  };

  return <AppContext.Provider value={value}>{children}</AppContext.Provider>;
};
